msg._originalPayload = msg.payload;
let flow_msg_variables = {
  tag: '',
  service: '',
  message: msg,
  date_created: Date.now()
}

var xTitle = "";
var xSubtitle = "";
var xMessage = "";
var xServices = "";
var xTag = "";
var xUrl = "";
var xCameraEntity = "";
var xInterruptionLevel = "";

// overrides
if (typeof msg.notificationOverride !== 'undefined' && msg.notificationOverride !== null) {
    if (typeof msg.notificationOverride.title !== 'undefined' && msg.notificationOverride.title !=='' && msg.notificationOverride.title !== null){
        xTitle = msg.notificationOverride.title;
    } else {
        xTitle = env.get('title');
    }
    if (typeof msg.notificationOverride.subtitle !== 'undefined' && msg.notificationOverride.subtitle !==''  && msg.notificationOverride.subtitle !== null){
        xSubtitle = msg.notificationOverride.subtitle;
    } else {
        xSubtitle = env.get('subtitle');
    }
    if (typeof msg.notificationOverride.message !== 'undefined' && msg.notificationOverride.message !=='' &&  msg.notificationOverride.message !== null){
        xMessage = msg.notificationOverride.message;
    } else {
        xMessage = env.get('message');
    }
    if (typeof msg.notificationOverride.url !== 'undefined' && msg.notificationOverride.url !=='' &&  msg.notificationOverride.url !== null){
        xUrl = msg.notificationOverride.url;
    } else {
        xUrl = env.get('notificationUrl');
    }
    if (typeof msg.notificationOverride.services !== 'undefined' && msg.notificationOverride.services !=='' && msg.notificationOverride.services !== null){
        xServices = msg.notificationOverride.services;
    } else {
        xServices = env.get('service');
    }
    if (typeof msg.notificationOverride.cameraEntity !== 'undefined' && msg.notificationOverride.cameraEntity !=='' && msg.notificationOverride.cameraEntity !== null){
        xCameraEntity = msg.notificationOverride.cameraEntity;
    } else {
        xCameraEntity = env.get('cameraEntity');
    }

    if (typeof msg.notificationOverride.interruptionLevel !== 'undefined' && msg.notificationOverride.interruptionLevel !=='' && msg.notificationOverride.interruptionLevel !== null){
        xInterruptionLevel = msg.notificationOverride.interruptionLevel;
    } else {
        xInterruptionLevel = env.get('interruptionLevel');
    }

    flow_msg_variables.service=xServices;
    if (typeof msg.notificationOverride.tag !== 'undefined' && msg.notificationOverride.tag !=='' && msg.notificationOverride.tag !== null){
        xTag = msg.notificationOverride.tag.replace(/[^\w\s]/gi, '').replace(/\s+/g,'_').toUpperCase();
    } else {
        if (env.get('tag') !== '') {
            xTag = `${env.get('tag').replace(/[^\w\s]/gi, '').replace(/\s+/g,'_').toUpperCase()}`;
        } else {
            // need to still set this to something in case clear_notification is sent.
            xTag = `${env.get('title').replace(/[^\w\s]/gi, '').replace(/\s+/g,'_').toUpperCase()}_${flow.get('random')}`;
        } 
    }
//support multiple tags per flow
  flow_msg_variables.tag=xTag;
} else {
    // If no override is sent in...
    if (env.get('tag') !== '') {
        xTag = `${env.get('tag').replace(/[^\w\s]/gi, '').replace(/\s+/g,'_').toUpperCase()}`;
    } else {
        // need to still set this to something in case clear_notification is sent.
        xTag = `${env.get('title').replace(/[^\w\s]/gi, '').replace(/\s+/g,'_').toUpperCase()}_${flow.get('random')}`;
    } 
    //support multiple tags per flow
    //  flow.set('tag',xTag);
    flow_msg_variables.tag=xTag;

    xServices = env.get('service');
    flow_msg_variables.service=xServices;

    xTitle = env.get('title');
    xSubtitle = env.get('subtitle');
    xMessage = env.get('message');
    xUrl = env.get('notificationUrl');
    xCameraEntity = env.get('cameraEntity');
    xInterruptionLevel = env.get('interruptionLevel');
}

let all_flow_messages= flow.get('flow_messages');
if (!all_flow_messages){
  all_flow_messages=[];
}
let new_flow_messages=[];
for(var i=0;i<all_flow_messages.length;i++){//copy (and reset) the messages array
  if ((all_flow_messages[i].tag==flow_msg_variables.tag)&&(all_flow_messages[i].service==flow_msg_variables.service)){
    continue;//don't copy this message
  }else{
    if (Date.now()-all_flow_messages[i].date_created<86400000){//only copy messages less than 24h old
      new_flow_messages.push(all_flow_messages[i]);
    }
  }
}
new_flow_messages.push(flow_msg_variables);
flow.set('flow_messages',new_flow_messages);

const services = xServices;
if(!services) {
    node.status({
        text: 'no services defined',
        shape: 'ring',
        fill: 'red'
    });
    return;    
} else if (services == "NOONE"){
    node.status({
        text: 'No one to send to',
        shape: 'ring',
        fill: 'yellow'
    });
    return;
}



// create actions
const actions = [];
[1,2,3,4].forEach(i => {
    const name = `action${i}`
    if (env.get(`${name}Title`) !== ''){
        const action = env.get(`${name}Title`).replace(/[^\w\s]/gi, '').replace(/\s+/g,'_').toUpperCase();
        const title = env.get(`${name}Title`);
        const activationMode = env.get(`${name}ActivationMode`);  

        const uri = env.get(`${name}Uri`);
        const textInputButtonTitle = env.get(`${name}TextInputButtonTitle`);
        const textInputPlaceholder = env.get(`${name}TextInputPlaceHolder`);
        const authenticationRequired = env.get(`${name}AuthenticationRequired`);
        const destructive = env.get(`${name}Destructive`);
        const behavior = env.get(`${name}Behavior`);
        const icon = env.get(`${name}Icon`);

        const actionObject = {};
        actionObject.action = action;
        actionObject.title = title;
        actionObject.activationMode = activationMode;

        if (uri != "") actionObject.uri = uri;
        if (textInputButtonTitle != "") actionObject.textInputButtonTitle = textInputButtonTitle;
        if (textInputPlaceholder != "") actionObject.textInputPlaceholder = textInputPlaceholder;
        if (authenticationRequired != "") actionObject.authenticationRequired = authenticationRequired;
        if (destructive != "") actionObject.destructive = destructive;
        if (behavior != "") actionObject.behavior = behavior;
        if (icon != "") actionObject.icon = icon;

        actions.push(actionObject);
    }
});

// create msg object

msg.payload = {
        domain: 'notify',
        data: {
            title: xTitle,
            message: xMessage,
            data: {
                push: {},
                tag: xTag
            }
        }
    };

if (actions.length > 0) {
    msg.payload.data.data.action_data = {tag: xTag};
    msg.payload.data.data.actions = actions;

}


// notification url
if (xUrl !== "") {
    msg.payload.data.data.url = {};
    msg.payload.data.data.url = xUrl;
}

// subtitle
if (xSubtitle !== '') {
    msg.payload.data.data.subtitle = xSubtitle;
}

// sound information
msg.payload.data.data.push.sound = {};
if (env.get('customSound').length > 0){
    msg.payload.data.data.push.sound.name = env.get('customSound');
} else if (env.get('customSoundPreInstalled').length > 0) {
  if(env.get('customSoundPreInstalled')=='none'){
    msg.payload.data.data.push.sound = 'none';
  }else{
    msg.payload.data.data.push.sound.name = env.get('customSoundPreInstalled');
  }
} else {
    msg.payload.data.data.push.sound.name = 'default';
}

if (env.get('isCriticalNotification')) {
    msg.payload.data.data.push.sound.critical = 1;
    msg.payload.data.data.push.sound.volume = 1.0;
    msg.payload.data.data.push["interruption-level"] = 'critical';
} else {
    // interruption level
    if (xInterruptionLevel !== '') {
        msg.payload.data.data.push["interruption-level"] = xInterruptionLevel;
        if (xInterruptionLevel == 'critical'){
            msg.payload.data.data.push.sound.critical = 1;
            msg.payload.data.data.push.sound.volume = 1.0;
        }
    }
}

// group thread id
if (env.get('group') !== '') {
    msg.payload.data.data.group = env.get('group');
}

// Map Information
if (env.get('latitudeFirst') != '0' && env.get('longitudeFirst') != '0'){
    msg.payload.data.data.action_data.latitude = env.get('latitudeFirst');
    msg.payload.data.data.action_data.longitude = env.get('longitudeFirst');
    if (env.get('latitudeSecond') != '0' && env.get('longitudeSecond') != '0'){
        msg.payload.data.data.action_data.second_latitude = env.get('latitudeSecond');
        msg.payload.data.data.action_data.second_longitude = env.get('longitudeSecond');
        // add in all the extras, which default to false.
        msg.payload.data.data.action_data.shows_line_between_points = env.get('showLineBetweenPoints');
        msg.payload.data.data.action_data.shows_compass = env.get('showCompass');
        msg.payload.data.data.action_data.shows_points_of_interest = env.get('showPointsOfInterest');
        msg.payload.data.data.action_data.shows_scale = env.get('showScale');
        msg.payload.data.data.action_data.shows_traffic = env.get('showTraffic');
        msg.payload.data.data.action_data.shows_user_location = env.get('showUserLocation');
    }
} 
// camera information
if (xCameraEntity !== '') {
    msg.payload.data.data.entity_id = xCameraEntity;
}
// media information
if (env.get('contentUrl') !== '') {

} else {
    if (env.get('imagePath') !== '') {
        msg.payload.data.data.image = env.get('imagePath');
    }
    if (env.get('videoPath') !== '') {
        msg.payload.data.data.video = env.get('videoPath');
    }
    if (env.get('audioPath') !== '') {
        msg.payload.data.data.audio = env.get('audioPath');
    }
}
if (env.get('contentUrl') !== '' || env.get('imagePath') !== '' || env.get('videoPath') !== '' || env.get('audioPath') !== '') {
    if (env.get('lazyLoading')) {
        msg.payload.data.data.lazy = env.get('lazyLoading');
    }
    if (env.get('hideThumbnail')) {
        msg.payload.data.data.attachment = {};
        msg.payload.data.data.attachment['hide-thumbnail'] = env.get('hideThumbnail');
    }
}

services.trim().split(/,\s*/).forEach(service => {
    if(!service) return;
    msg.payload.service = service;
    node.send(msg);
});

node.done();

const latestMessage = flow.get('latestMessage');
const event = msg.payload.event;

latestMessage.payload = latestMessage._originalPayload;
latestMessage.eventData = msg.payload;
delete latestMessage._originalPayload;

if(env.get('userInfo')) {
    const userData = msg.userData.find(u => u.id === msg.payload.context.user_id);
    latestMessage.userData = userData;
}

const index = [1,2,3,4].find(i => env.get(`action${i}Title`).replace(/[^\w\s]/gi, '').replace(/\s+/g,'_').toUpperCase() === event.actionName);
flow.set("responseIndex",index);
node.status({
    text: `${env.get(`action${index}Title`).replace(/[^\w\s]/gi, '').replace(/\s+/g,'_').toUpperCase()} at: ${getPrettyDate()}`,
    shape: 'dot',
    fill: 'green'
});

return latestMessage;


function getPrettyDate() {
    return new Date().toLocaleDateString('en-US', {
        month: 'short',
        day: 'numeric',
        hour12: false,
        hour: 'numeric',
        minute: 'numeric',
    });
}

msg._originalPayload = msg.payload;
var tag_to_clear = "";
var services = "";
var clearAll = false;
if (typeof msg.notificationOverride !== 'undefined') {
    if (typeof msg.notificationOverride.clear !== 'undefined' && msg.notificationOverride.clear !== null && msg.notificationOverride.clear) {
        clearAll = true;
    }
    if (typeof msg.notificationOverride.services !== 'undefined' && msg.notificationOverride.services !== null && msg.notificationOverride.services){
        services = msg.notificationOverride.services;
    }
    if (typeof msg.notificationOverride.tag !== 'undefined' && msg.notificationOverride.tag !== null && msg.notificationOverride.tag){
        tag_to_clear = msg.notificationOverride.tag.replace(/[^\w\s]/gi, '').replace(/\s+/g,'_').toUpperCase();//formatting tags as sent to the phone before
    }
}

if (tag_to_clear==""){//no specific tag sent, clear the last message.
  var all_flow_messages= flow.get('flow_messages');
  if(all_flow_messages) {
    var last_message= all_flow_messages.pop();
    if (last_message){//on nodeRED restart
      tag_to_clear= last_message.tag;
      services = last_message.service;//override service to the one used in last message sent
      flow.set('flow_messages',all_flow_messages);//clear the message from memory
    }
  }
}

if(services=="") {
    node.status({
        text: 'no services defined',
        shape: 'ring',
        fill: 'red'
    });
    return;    
}

if(tag_to_clear=="") {
  node.status({
    text: 'no messages to delete',
    shape: 'ring',
    fill: 'red'
  });
  return;  
}

// create ios msg object

msg.payload = {
        domain: 'notify',
        data: {
            message: "clear_notification",
            data: {
                tag: tag_to_clear
            }
        }
    };

function getPrettyDate() {
    return new Date().toLocaleDateString('en-US', {
        month: 'short',
        day: 'numeric',
        hour12: false,
        hour: 'numeric',
        minute: 'numeric',
    });
}

if (clearAll){
    delete msg.notificationOverride;
}

var xCountCleared = 0;
services.trim().split(/,\s*/).forEach(service => {
    if(!service) return;
    // only clear on devices that didn't send the event.
    if (clearAll){
        msg.payload.service = service;
        node.send(msg);
        xCountCleared++;
    } else if (!service.includes(msg._originalPayload.event.sourceDeviceID)){
        msg.payload.service = service;
        node.send(msg);
        xCountCleared++;
    }
});
if (xCountCleared > 0) {
    node.status({
        text: `${xCountCleared} messages cleared at: ${getPrettyDate()}`,
        shape: 'dot',
        fill: 'blue'
    });
} else {
    node.status({
        text: `No messages cleared: ${getPrettyDate()}`,
        shape: 'dot',
        fill: 'red'
    });
}

let msg_tag=msg.payload.event.action_data.tag;
let all_flow_messages= flow.get('flow_messages');
if(all_flow_messages){
    for (var i=0;i<all_flow_messages.length;i++){
        if (all_flow_messages[i].tag.trim()==msg_tag.trim()){
            flow.set('latestMessage',all_flow_messages[i].message);
            return [msg,null];
        }
    }
}
return [null,msg];
